    public static void mostrarInformacion(int idLlamada, int idPregunta){
        try{
            Statement s = control.Main.getCrearConexion().getConexion().createStatement();
            ResultSet rs = s.executeQuery ("CALL getLlamadaPregunta("+idLlamada+
                    ", "+idPregunta+")");
            //ResultSet rs = s.executeQuery ("CALL getLlamadaPregunta(1,1)");
            System.out.println("Aqui");
            while(rs.next()){
                textClienteId.setText(rs.getString(1));
                textClienteNombre.setText(rs.getString(24));
                textLlamadaId.setText(rs.getString(3));
                textLlamadaFecha.setText(rs.getString(8));
                textLlamadaTiempoE.setText(rs.getString(10));
                textLlamadaServicio.setText(rs.getString(11));
                textLlamadaTiempoC.setText(rs.getString(13));
                textLlamadaEstado.setText(rs.getString(12));
                textLlamadaSatisfaccion.setText(rs.getString(14));
                textLlamadaResolucion.setText(rs.getString(15));
                textPreguntaId.setText(rs.getString(4));
                textPreguntaFecha.setText(rs.getString(8));
                textPreguntaCanal.setText(rs.getString(6));
                textPreguntaEstado.setText(rs.getString(7));
                textLlamadaDetalle.setText(rs.getString(5));
            }
            rs = s.executeQuery ("CALL getDatosRespuesta("+idLlamada+
                    ", "+idPregunta+")");
            while(rs.next()){
                textRespuestaId.setText(rs.getString(1));
                textRespuestaFecha.setText(rs.getString(48));
                textRespuestaDetalle.setText(rs.getString(47));
            }

        }catch (Exception e){
            System.out.println(e);
        }
    }
